package test;

public class VariableTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Data types
		int i =10;
		System.out.println(i);
		
		i =20;
		System.out.println(i);
		i=i+10;
		System.out.println(i);
		i=i*2;
		System.out.println(i);
		System.out.println(i++);
		System.out.println(i);
		System.out.println(++i);
		
		//char
		char a = 'b';
		System.out.println(a);
		
		a = 'p';
		System.out.println(a);
		
		//double
		double p = 10.15;
		System.out.println(p);
		
		
		
		
		

	}

}
